class Solution:
    def shuffle(self, nums, n):
        new_list = []
        a = 0
        check = False
        while check is False:
            if n < len(nums):
                new_list.append(nums[a])
                print(new_list)
                new_list.append(nums[n])
                print(new_list)
                a += 1
                n += 1

            else:
                check = True

        return new_list
    
